const goToSectionItems = [...document.querySelectorAll('.go-to span')]

if (!goToSectionItems.length) {

} else {

    goToSectionItems.forEach((elem) => {
        elem.addEventListener('click', function () {
            const block = elem.getAttribute('data-go')
            document.querySelector('' + block).scrollIntoView({
                behavior: "smooth",
                block: "start"
            })
        })
    })

    window.addEventListener('load', goTo)

    window.addEventListener('scroll', goTo)

    function goTo() {
        const section = [...document.querySelectorAll('section')]

        const sectionPos = section.map(elem => elem.offsetTop)

        const sectionName = ['Агроклімат', 'Про нас', 'Наші послуги', 'Наша команда',
            'Наші винагороди', 'Вакансії']

        let itemOffset = []
        let windowOffset = window.pageYOffset

        for (let i = 0; i < sectionPos.length; i++) {
            itemOffset.push(sectionPos.slice(i, i + 2))
        }

        itemOffset.forEach((elem, idx) => {
            goToSectionItems[idx].classList.remove('active')
            goToSectionItems[idx].innerHTML = idx + 1
            if (elem[0] - 3 <= windowOffset && elem[1] - 3 > windowOffset) {
                goToSectionItems[idx].classList.add('active')
                goToSectionItems[idx].innerHTML = sectionName[idx]
            } else if (elem[0] - 3 <= windowOffset && !elem[1]) {
                goToSectionItems[idx].classList.add('active')
                goToSectionItems[idx].innerHTML = sectionName[idx]
            }
        })
    }
}

let allLazyLoad = [...document.querySelectorAll('.lazyload')];

function allLozadImg() {
    allLazyLoad.forEach((el) => {
        const observer = lozad(el); // passing a `NodeList` (e.g. `document.querySelectorAll()`) is also valid
        observer.observe();
        el.addEventListener('load', () => {
            el.classList.add('is-loaded')
        })
    })
}

allLozadImg();

const showVacanciesBtn = [...document.querySelectorAll('.vacancies__list .list__item .btn')]

if (!showVacanciesBtn.length) {

} else {
    showVacanciesBtn.forEach(el => {
        el.addEventListener('click', function (e) {
            e.preventDefault()

            let content = el.parentElement.nextElementSibling

            if (!content.parentElement.classList.contains('active')) {
                document.querySelectorAll('.vacancies__list .list__item .content').forEach(el => {
                    el.parentElement.classList.remove('active')
                })
                this.parentElement.parentElement.classList.add('active')
            } else {
                this.parentElement.parentElement.classList.remove('active')
            }
        })
    })
}

const orderServiceBtn = [...document.querySelectorAll('.order-service')]

if (!orderServiceBtn.length) {

} else {
    orderServiceBtn.forEach(elem => {
        elem.addEventListener('click', function (e) {
            e.preventDefault()

            const orderPopup = document.querySelector('.services-popup')
            const orderPopupClose = document.querySelector('.services-popup .close-popup')
            const body = document.querySelector('body')

            orderPopup.addEventListener('click', function (e) {
                const item = e.target
                if (item.classList.contains('services-popup--visible')) {
                    orderPopup.classList.remove('services-popup--visible')
                    body.classList.remove('no-scroll')
                }
            })

            orderPopup.classList.add('services-popup--visible')
            body.classList.add('no-scroll')

            orderPopupClose.addEventListener('click', function () {
                orderPopup.classList.remove('services-popup--visible')
                body.classList.remove('no-scroll')
            })
        })
    })
}

const sendResumeBtn = [...document.querySelectorAll('.vacancies .send-resume')]

if (!sendResumeBtn.length) {

} else {
    sendResumeBtn.forEach(elem => {
        elem.addEventListener('click', function (e) {
            e.preventDefault()

            const vacanciesPopup = document.querySelector('.vacancies-popup')
            const vacanciesPopupClose = document.querySelector('.vacancies-popup .close-popup')
            const vacanciesPopupUploadFile = document.querySelector('.vacancies-popup .vacancies-popup__wrapper input[type=file]')
            const body = document.querySelector('body')

            vacanciesPopup.addEventListener('click', function (e) {
                const item = e.target
                if (item.classList.contains('vacancies-popup--visible')) {
                    vacanciesPopup.classList.remove('vacancies-popup--visible')
                    body.classList.remove('no-scroll')
                }
            })

            vacanciesPopupUploadFile.addEventListener('change', function () {
                document.querySelector('.vacancies-popup .vacancies-popup__wrapper .file-name').innerHTML = this.files[0].name
            })
            vacanciesPopup.classList.add('vacancies-popup--visible')
            body.classList.add('no-scroll')

            vacanciesPopupClose.addEventListener('click', function () {
                vacanciesPopup.classList.remove('vacancies-popup--visible')
                body.classList.remove('no-scroll')
            })

        })
    })
}

function helloConsole() {
    const staticText = 'Made with ❤️ by Demiweb';
    const staticUrl = '> https://demiweb.pro/';
    const hello = [`\n %c ${staticText} %c ${staticUrl} %c %c \n\n`, 'border: 1px solid #000;color: #fff; background: #3d2579; padding:5px 0;', 'color: #fff; background: #ffd800; padding:5px 0;border: 1px solid #000;', 'background: #fff; padding:5px 0;', 'color: #b0976d; background: #fff; padding:5px 0;'];

    if ((navigator.userAgent.indexOf("MSIE") != -1) || (!!document.documentMode == true)) {
        window.console.log(`${staticText} - ${staticUrl}`);
    } else if (navigator.userAgent.indexOf("Chrome") != -1) {
        let title = '%c';

        for (let i = 0; i < 12; i++) {
            title += ' ';
        }

        window.console.log.apply(console, hello);
        window.console.log(title, 'font: 36px/100% sans-serif; background-repeat: no-repeat; background-size: 100%; background-image: url(data:image/svg+xml;base64,);');
    } else {
        const title = 'demiweb';
        const banner = `\n%c${title.split('').join('%c %c')}%c\n`;
        const styler = [];

        const symbol = [
            'font: 32px/1.5 Helvetica, Arial, sans-serif',
            'text-transform: uppercase',
            'color: #fff',
            'background-color: #ffd800',
            'padding: 4px 14px',
        ].join(';');

        title.split('').forEach(() => {
            styler.push(symbol);
            styler.push('');
        });

        window.console.log(`${staticText} - ${staticUrl}`);
        window.console.log(banner, ...styler);
    }
}

helloConsole();

